const dnsZone = config.require("dnsZone");
const publicIp = new scaleway.InstanceIp("publicIp", {projectId: projectId});
const web = new scaleway.InstanceServer("web", {
    projectId: projectId,
    type: "DEV1-S",
    image: "ubuntu_jammy",
    tags: [
        "front",
        "web",
    ],
    ipId: publicIp.id,
    rootVolume: {
        sizeInGb: 20,
    },
});
