    dnsZone: dnsZone,
    type: "A",
    data: web.publicIp,
    ttl: 3600,
});
const webCname = new scaleway.DomainRecord("webCname", {
    dnsZone: dnsZone,
    type: "CNAME",
    data: `web.${dnsZone}.`,
    ttl: 3600,
});
const webAlias = new scaleway.DomainRecord("webAlias", {
    dnsZone: dnsZone,
    type: "ALIAS",
    data: `web.${dnsZone}.`,
    ttl: 3600,
});
