    ipId: ip01.id,
    type: "lb-s",
});
const bkd01 = new scaleway.LoadbalancerBackend("bkd01", {
    lbId: lb01.id,
    forwardProtocol: "tcp",
    forwardPort: 80,
    proxyProtocol: "none",
});
