    lbId: lb01.id,
    backendId: bkd01.id,
    inboundPort: 80,
});
const rt01 = new scaleway.LoadbalancerRoute("rt01", {
    frontendId: frt01.id,
    backendId: bkd01.id,
    matchSni: "sni.scaleway.com",
});
