func main() {
	pulumi.Run(func(ctx *pulumi.Context) error {
		premium, err := scaleway.GetCockpitPlan(ctx, &scaleway.GetCockpitPlanArgs{
			Name: "premium",
		}, nil)
		if err != nil {
			return err
		}
		_, err = scaleway.NewCockpit(ctx, "main", &scaleway.CockpitArgs{
			Plan: pulumi.String(premium.Id),
		})
