                    .ip("212.47.225.64")
                    .build(),
                InstanceSecurityGroupInboundRuleArgs.builder()
                    .action("accept")
                    .port("80")
                    .build(),
                InstanceSecurityGroupInboundRuleArgs.builder()
                    .action("accept")
                    .port("443")
                    .build())
            .outboundRules(InstanceSecurityGroupOutboundRuleArgs.builder()
                .action("drop")
                .ipRange("10.20.0.0/24")
